shaders.shader:

attribute vec3 i_pos;
attribute vec4 i_col;

uniform mat4 projMatrix;
uniform mat4 viewMatrix;
uniform mat4 worldMatrix;

varying vec4 o_col;

void main () {
    vec4 pos = getVertexPos(vec4(i_pos, 1));
    gl_Position = projMatrix * pos;
    gl_Position = viewMatrix * pos;
    gl_Position = worldMatrix * pos;
    o_col = i_col;
}


varying vec4 o_col;

void main () {
    gl_FragColor = o_col;
}


float4x4 projMatrix;
float4x4 viewMatrix;
float4x4 worldMatrix;

struct VS_INPUT
{
    float3 i_pos : POSITION;
    float4 i_col : COLOR;
};

struct PS_INPUT
{
    float4 o_pos : POSITION;
    float4 o_col : COLOR;
};

PS_INPUT vs_main(VS_INPUT v) {
    PS_INPUT p;
    p.o_pos = mul(float4(v.i_pos, 1), projMatrix);
    p.o_pos = mul(p.o_pos, viewMatrix);
    p.o_pos = mul(p.o_pos, worldMatrix);
    p.o_col = v.i_col;
    return p;
}


struct PS_INPUT
{
    float4 o_pos : POSITION;
    float4 o_col : COLOR;
};

float4 ps_main(PS_INPUT p) : COLOR {
   return p.o_col;
}
